def _parse_keywords(keywords_str: str, default_weight: float = 0.8, default_type: str = "partial") -> list:
    """
    Parse comma-separated keywords string into keyword dicts.
    
    Args:
        keywords_str: Comma-separated string of keywords
        default_weight: Default weight for keywords (0.0 to 1.0)
        default_type: Default type (exact, partial, regex)
        
    Returns:
        List of keyword dicts
    """
    keywords_list = [kw.strip() for kw in keywords_str.split(",") if kw.strip()]
    
    keyword_dicts = []
    for keyword in keywords_list:
        # Assign weights based on keyword length and specificity
        # Shorter, more specific keywords get higher weight
        # Longer phrases get slightly lower weight
        if len(keyword.split()) == 1:
            weight = min(1.0, default_weight + 0.1)  # Single word: higher weight
        elif len(keyword.split()) <= 3:
            weight = default_weight  # Short phrase: default weight
        else:
            weight = max(0.6, default_weight - 0.1)  # Long phrase: slightly lower
        
        keyword_dicts.append({
            "keyword": keyword,
            "weight": round(weight, 2),
            "type": default_type,
            "active": True,
        })
    
    return keyword_dicts


